import React from "react";

class TopBar extends React.Component{

    ClickHandler(){
        this.setState({
            Correct: this.state.Correct += 1
        })
    }


    render(){
        return(
            <div className="TopBar">
                <h1 className="Correct" style={{color: this.state.Color[0]}}>Correct: {this.state.Correct}</h1>
                <h1 className="Wrong" style={{color: this.state.Color[1]}}>Wrong: {this.state.Wrong}</h1>
            </div>
        )
    }
}

export default TopBar;
